client_script:
$('main').addClass('card voting-card');
$('.awesomplete>ul').css('position', 'static');


// Populate college field
const collegeName = decodeURIComponent(window.location.hash.substr(1));
frappe.web_form.set_value("college", collegeName);

document.getElementsByTagName("input")[1].maxLength = 10;
document.getElementsByTagName("input")[1].minLength = 10;

frappe.web_form.after_load = () => {
    frappe.call({
        method: "frappe.client.get_value",
        args: {
            "doctype": "Vote",
            "filters": {"voter": frappe.session.user},
            "fieldname": "name"
        },
        callback: function(r) {
            if (!r.exc) {
                if (r.message.name) {
                    frappe.msgprint("You have already Voted!");
					window.location.replace("/leaderboard");
				}
            }
        }
    });

    filterCollege();
}

function filterCollege() {
    var url = 'api/resource/College?filters=[["College","approval_status","=","Approved"]]';
	$.ajax({
		type: 'GET', 
		url: url,
		async: false,
		success: function(result) {
			var options = [];
			for (var i = 0; i < result.data.length; i++) {
				options.push({
					'label': result.data[i].name,
					'value': result.data[i].name
				});
			}
			var field = frappe.web_form.get_field('college');
			field._data = options;
			field.refresh();
		}
	});
}

frappe.web_form.after_save = () => {
    frappe.msgprint({
        message: "Your vote was successfully submitted. Thank you for voting!",
        title: "Success",
        indicator: "green"
    });
    setTimeout(() => {
	    window.location.replace("/leaderboard");	
	}, 3000);
}

frappe.web_form.cancel = () => {
    window.location.replace('/colleges');
}

