javascript:
$('.college-hero').find('.btn')
    .removeClass('btn-primary')
    .addClass('btn-secondary');

$('.section-cta').find('.btn')
    .removeClass('btn-primary')
    .addClass('btn-secondary');

// temporarily disable sorting by votes
var options = {
    "College Name": "name",
    "College Location": "location"
};

var $sort = $("#input-sort");
$sort.empty(); // remove old options
$.each(options, function(key, value) {
  $sort.append($("<option></option>")
     .attr("value", value).text(key));
});
