theme_scss:
@import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@300;400;500;600;700;800&display=swap");
$font-family-sans-serif: "Montserrat", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif;
$primary: #75AC39;$enable-shadows: false;
$enable-gradients: false;
$enable-rounded: true;

// Bootstrap Variable Overrides
$success: #75AC39;
$secondary: #E28B22;

:root {
    --green: #75AC39;
    --orange: #E28B22;
    --light-green: #E4F1D7;
    --light-orange: #FDF8F1;
}

.icon {
    --icon-stroke: #FFF;
}

// Import themes from installed apps
@import "frappe/public/scss/website.bundle";
@import "bhumi_awards_portal/public/scss/website";



// Custom Theme
.web-footer {
    background-color: var(--light-green);
}

.vote-count {
    color: var(--orange);
}

.btn.btn-secondary {
    background-color: $secondary;
    color: #fff;
}

.btn.btn-secondary:hover {
    background-color: lighten($secondary, 5%);
    color: #fff;
}

#refresh-button:hover {
    background-color: lighten($secondary, 5%);
}

h1 {
    font-family: "Montserrat", serif;
}

h2 {
    font-family: "Montserrat", sans-serif;
}

.display-4 {
    font-size: 2rem;
}

:root {

	--primary: #{$primary};
	--primary-color: #{$primary};
	}
